from typing import List
from collections import Counter


class Solution:
    @staticmethod
    def findSubstringInWraproundString(p: str) -> int:
        letter_counter = Counter()
        n = len(p)
        letter_counter[p[0]] = 1
        max = 1
        for j in range(1, n):
            diff = ord(p[j]) - ord(p[(j-1)])
            if  diff == 1 or diff == -25:
                max += 1
            else:
                max = 1
            if max > letter_counter[p[j]]:
                letter_counter[p[j]] = max
        return sum(letter_counter.values())


if __name__ == '__main__':
    p = ""
    print(Solution.findSubstringInWraproundString(p))
